import { NIMCommonError, NIMStrAnyObj } from '../types';
/**
 * 服务代理相关 API
 */
export interface PassThroughInterface {
    /**
     * 透传协议
     */
    httpRequestProxy(options: NIMHttpRequestProxyOptions): void;
}
export interface NIMHttpRequestProxyOptions {
    /**
     * HTTP path（不包含域名 host）
     */
    path: string;
    /**
     * 映射一个视频云 upstream host，不传用默认配置
     */
    zone?: string;
    /**
     * HTTP Method
     *
     * 数字常量 (1-get, 2-post, 3-put, 4-delete)，默认post
     */
    method?: number;
    /**
     * HTTP header，JSON 格式字符串
     */
    header?: string;
    /**
     * HTTP body，透传字符串
     */
    body?: string;
    done?: (err: NIMCommonError | Error | NIMStrAnyObj | null, data: NIMHttpRequestProxyOptions) => void;
}
